mapper.select:
  	SELECT 

	from PERMISSION_RECORD


    		 CUSTOMER_Id = #{permRecordModel.customerId}
    		 AND  RESOURCE_ID = #{permRecordModel.resourceId}
    		 AND  CGROUP_ID = #{permRecordModel.cgroupId}
    		 AND  RGROUP_ID = #{permRecordModel.rgroupId}
    		 AND  OBJECT_RELATION = #{permRecordModel.objectRelation}
    		 AND  ACTION_TYPE = #{permRecordModel.actionType}


    ORDER BY CREATE_DATE DESC
