    limit #{startRow,jdbcType=INTEGER}, #{pageSize,jdbcType=INTEGER}

select 
count(1) AS totalCount
from ROLE A LEFT JOIN BUSER_ROLE B ON A.ID=B.ROLE_ID AND B.USER_ID=#{userId,jdbcType=BIGINT} 
where A.NAME like #{nameLike}

